mapper.select:
SELECT p.order_num AS orderNum , e.empl_name AS emplName , e.empl_email AS email , pt.partName AS partName , p.order_quantity AS orderQuantity , i.inspection_num AS inspectionNum , i.inspection_date AS inspectionDate , i.inspection_quantity AS inspectionQuantity , i.sample_quantity AS sampleQuantity , CASE WHEN (SELECT COUNT(*) FROM inspection ip INNER JOIN inspection_result ipr ON ip.order_num = ipr.order_num AND ip.inspection_num = ipr.inspection_num WHERE ip.order_num = p.order_num) != 0 THEN '종료'
WHEN sysdate < i.inspection_date THEN '시작 전' WHEN sysdate > p.due_date THEN '종료 후'
ELSE '진행 중' END AS progress FROM inspection i RIGHT JOIN purchase_order p ON p.order_num = i.order_num INNER JOIN employee e ON p.empl_num = e.empl_num INNER JOIN tbl_contract ct ON p.contract_num = ct.contractno INNER JOIN tbl_part pt ON ct.partcode = pt.partcode INNER JOIN tbl_support sp ON sp.supportno = p.support_num WHERE p.order_num = #{ orderNum }
AND i.inspection_num = #{ inspectionNum }
OR i.inspection_num = #{ unitNum }
ORDER BY i.inspection_num ASC
