mapper.select:
SELECT pt.partname AS partName , e.empl_name AS emplName , e.empl_email AS email , p.order_num AS orderNum , cp.name AS companyName , p.scheduled_date AS scheduledDate , sp.deliverydate AS dueDate , p.order_quantity AS orderQuantity , NVL((SELECT SUM(i.inspection_quantity) AS completeQuantity FROM inspection i WHERE i.order_num = p.order_num AND i.inspection_num IN (SELECT ir.inspection_num FROM inspection_result ir WHERE i.order_num = ir.order_num) GROUP BY i.order_num), 0) AS completeQuantity FROM purchase_order p INNER JOIN employee e ON p.empl_num = e.empl_num INNER JOIN tbl_contract ct ON p.contract_num = ct.contractno INNER JOIN tbl_part pt ON ct.partcode = pt.partcode INNER JOIN tbl_company cp ON ct.code = cp.code INNER JOIN tbl_support sp ON p.support_num = sp.supportno
AND pt.partname LIKE '%'||#{ partName }||'%'
AND e.empl_name LIKE '%'||#{ emplName }||'%'
AND cp.name LIKE '%'||#{ companyName }||'%'
AND #{ initialScheduledDate } < p.scheduled_date AND p.scheduled_date < #{ finalScheduledDate }
AND sysdate < p.scheduled_date AND p.scheduled_date < #{ finalScheduledDate }
AND sysdate < p.scheduled_date AND p.scheduled_date < sysdate
AND #{ initialDueDate } < sp.deliverydate AND sp.deliverydate < #{ finalDueDate }
AND sysdate < sp.deliverydate AND sp.deliverydate < #{ finalDueDate }
AND #{ initialDueDate } < sp.deliverydate AND sp.deliverydate < sysdate
