mapper.select:
SELECT tbl.planNum AS planNum , tbl.requirement AS requirement , tbl.dueDate AS dueDate , tbl.partname AS partName FROM (SELECT sp.supportno AS planNum , sp.requirement AS requirement , sp.deliverydate AS dueDate , pt.partname AS partName , ROWNUM as rn FROM tbl_support sp INNER JOIN tbl_part pt ON sp.partcode = pt.partcode
ORDER BY sp.deliverydate DESC, sp.supportno) tbl
WHERE (#{ curPageDto.pageNum } - 1) * #{ curPageDto.amount } < tbl.rn AND tbl.rn <= (#{ curPageDto.pageNum }) * #{ curPageDto.amount }

SELECT (SELECT cp.currency FROM tbl_company cp WHERE cp.code = (SELECT ct.code FROM tbl_contract ct WHERE ct.contractno = p.contract_num)) AS contractCurrency , (SELECT SUM(p.order_quantity * (SELECT ct.unitprice FROM tbl_contract ct WHERE ct.contractno = p.contract_num)) AS totalPrice FROM purchase_order p GROUP BY p.support_num) AS totalPrice FROM purchase_order p WHERE p.support_num = #{ planNum }
UNION ALL
SELECT 'other' AS contractCurrency, 0 AS totalPrice FROM DUAL WHERE NOT EXISTS (SELECT * FROM purchase_order p WHERE p.support_num = #{ planNum } GROUP BY p.support_num)

SELECT NVL(sp.requirement - (SELECT SUM(p.order_quantity) FROM purchase_order p WHERE p.support_num = sp.supportno GROUP BY p.support_num), sp.requirement)
FROM tbl_support sp
WHERE supportno = #{ planNum }
