mapper.select:
SELECT pl.plan_num AS planNum , pd.product_num AS productNum , pd.product_name AS productName , pl.epl_num AS eplNum , pl.quantity AS quantity , pl.due_date AS dueDate , p.ptn_num AS ptnNum , p.ptn_name AS ptnName , p.ptn_tel AS ptnTel , p.ptn_fax AS ptnFax , pd.product_price AS productPrice , e.member_id AS memberId , e.email AS email FROM ((plan AS pl INNER JOIN product AS pd ON pl.product_num = pd.product_num) LEFT JOIN employee AS e ON pl.epl_num = e.epl_num) INNER JOIN partner AS p ON p.ptn_num = pd.ptn_num
and pl.plan_num = #{ planNum }
and pl.epl_num = #{ eplNum }
and pl.due_date = #{ dueDate }
and pd.ptn_num = #{ ptnNum }

SELECT
	COUNT(*)
FROM plan
WHERE epl_num = #{ eplNum }
GROUP BY epl_num;

mapper.update:
UPDATE plan
SET epl_num = #{ eplNum }, quantity = #{ quantity }, due_date = #{ dueDate }
WHERE plan_num = #{ planNum }
